namespace MagicCard.Library.Interfaces{  /// <summary>  /// Interface for card implementations.  /// </summary>  public interface ICard  {    /// <summary>    /// Account number associated with the card.    /// </summary>    string AccountNumber { get; }    /// <summary>    /// Current available balance on the card.    /// </summary>    decimal Balance { get; }    /// <summary>    /// Top-up the card with an amount.    /// </summary>    /// <param name="amount">The amoubnt to add to the balance.</param>    /// <returns></returns>    decimal Deposit(decimal amount);    /// <summary>    /// Withdraw an amount from the card.    /// </summary>    /// <param name="pin">The pin number to verify.</param>    /// <param name="amount">The amount to withdraw.</param>    /// <returns>The remaining balance.</returns>    decimal Withdraw(string pin, decimal amount);  }}